def get(params):
    """get all airports"""
    res = service_api.get_airports(params)
    result = res.get('result', {})

    if params.get('code'):
        airport = filter_dict(
            result, ['code', 'name', 'description', 'coordinates'])
        return {'airport': airport}

    locations = result.get('_embedded', {}).get('locations', [])
    page = result.get('page', {})
    airports = filter_dict_list(
        locations, ['code', 'name', 'description', 'coordinates'])
    return {'airports': airports, 'page': page}

