def _get_airport_list():
    airport_list = []
    for page in range(1, 43):
        res = service_api.get_airports({'page': page})
        result = res.get('result', {})
        locations = result.get('_embedded', {}).get('locations', [])
        airports = filter_dict_list(locations, ['code', 'name', ])
        airport_list += airports
    print(json.dumps(airport_list))
    return {'list': airport_list}
